// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/interfaces/IERC165.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Capped.sol";
import "@faircrypto/xen-crypto/contracts/interfaces/IBurnRedeemable.sol";
import "@faircrypto/xen-crypto/contracts/interfaces/IBurnableToken.sol";
// import "./interfaces/XENAware.sol";
// import "./interfaces/Collectable.sol";


contract AuctionedToken is IERC165, Ownable, ERC20, ERC20Capped, IBurnRedeemable, IBurnableToken  {

    // IBurnableToken public immutable xenCrypto;
    uint256 public immutable batchSize;
    uint256 public immutable cycleInterval;
    uint256 public immutable startBlockNumber;
    // uint256 public immutable royaltyPct;
    address public immutable factory;

    uint256 public currentCycle = 1;
    mapping(uint256 => mapping(address => uint256)) public bids;
    mapping(uint256 => uint256) public totals;
    mapping(address => uint256) public lastProcessedCycles;
    mapping(address => uint256) public userBurns;

    constructor(
        string memory name_,
        string memory symbol_,
        uint256 supply_,
        uint256 batchSize_,
        uint256 cycleInterval_,
        uint256 startBlockNumber_
        // uint256 royaltyPct_
    )
        ERC20(name_, symbol_)
        ERC20Capped(supply_)
    {
        require(bytes(symbol_).length > 3, 'FERC-20: 4 or more letters required for symbol');
        require(supply_ > 0, 'FERC-20: supply shall be > 0');
        require(batchSize_ > 0, 'FERC-20: batchSize shall be > 0');
        require(batchSize_ <= supply_, 'FERC-20: batchSize shall be <= supply');
        require(cycleInterval_ > 0, 'FERC-20: cycleInterval shall be > 0');
        // require(royaltyPct_ < 6, 'FERC-20: royaltyPct shall be 0..5');
        batchSize = batchSize_;
        cycleInterval = cycleInterval_;
        startBlockNumber = startBlockNumber_ > 0 ? startBlockNumber_ : block.number;
        // xenCrypto = IBurnableToken(xenCrypto_);
        factory = msg.sender;
        // royaltyPct = royaltyPct_;
    }

    // INTERFACES & STANDARDS
    // IERC165 IMPLEMENTATION

    /**
        @dev confirms support for IERC-165 and IBurnRedeemable interfaces
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return interfaceId == type(IBurnRedeemable).interfaceId
            || interfaceId == type(IBurnableToken).interfaceId
            || interfaceId == type(IERC165).interfaceId
        ;
    }

    // IBurnRedeemable IMPLEMENTATION

    function onTokenBurned(address user, uint256 amount) external {
        // address xenCrypto = XENAware(factory).xenCrypto();
        // require(msg.sender == xenCrypto, "FERC-20: illegal callback caller");
        require(msg.sender == factory, "FERC-20: illegal callback caller");
        bids[currentCycle][user] += amount;
        totals[currentCycle] += amount;
    }

    function _mint(address to, uint256 amount) override (ERC20, ERC20Capped) internal {
        super._mint(to, amount);
    }

    function bid(uint256 amount) external {
        require(amount > 0 , 'FERC-20: amount shall be > 0');
        require(tx.origin == msg.sender, 'FERC-20: only EOAs allowed');

        if (block.number >= startBlockNumber + currentCycle * cycleInterval) {
            currentCycle++;
        }
        /*
        address xenCrypto = XENAware(factory).xenCrypto();
