        ERC20(xenCrypto).approve(factory, amount);
        IBurnableToken(factory).burn(msg.sender, amount);
        */
    }

    function mint() external {
        uint256 total;
        for (uint i = currentCycle - 1; i > lastProcessedCycles[msg.sender]; i--) {
            if (totals[i] > 0) {
                total += ((bids[i][msg.sender] * batchSize) / totals[i]);
            }
        }
        lastProcessedCycles[msg.sender] = currentCycle - 1;
        if (total > 0) {
            _mint(msg.sender, total);
        }
    }

