// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/interfaces/IERC165.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@faircrypto/xen-crypto/contracts/XENCrypto.sol";
import "@faircrypto/xen-crypto/contracts/interfaces/IBurnRedeemable.sol";
import "@faircrypto/xen-crypto/contracts/interfaces/IBurnableToken.sol";
import "./AuctionedToken.sol";
import "./interfaces/XENAware.sol";
import "./interfaces/Capped.sol";
import "./XENVarStaker.sol";

contract XENTokenizer is IERC165, XENAware, IBurnRedeemable {

    address public immutable xenCrypto;
    address public immutable xenVarStaker;

    // all tokens
    string[] public allTokens;
    // token symbol => token mapping
    mapping(bytes32 => address) public tokens;
    // token => deployer mapping
    mapping(address => address) public deployers;
    // token => burned XEN mapping
    mapping(address => uint256) public burnByTokens;
    // token => user => burned XEN mapping
    mapping(address => mapping(address => uint256)) public burnByUsers;

    // state management + guard
    address private _caller;

    constructor(address xenCrypto_, address xenVarStaker_) {
        require(xenCrypto_ != address(0), 'XF: bad XEN Crypto address');
        require(xenVarStaker_ != address(0), 'XF: bad XEN VarStaker address');
        xenCrypto = xenCrypto_;
        xenVarStaker = xenVarStaker_;
    }

    function tokenCount() external view returns (uint256 count) {
        return allTokens.length;
    }

    function getTokenAddress(string memory symbol_) public view returns (address) {
        bytes32 key = keccak256(abi.encodePacked(symbol_));
        return tokens[key];
    }

    function getTokenInfo(string memory symbol_) external view returns (address token, address deployer, uint256 burns) {
        token = getTokenAddress(symbol_);
        deployer = deployers[token];
        burns = burnByTokens[token];
    }

    function getUserBurn(string memory symbol_) external view returns (uint256 userBurn) {
        address token = getTokenAddress(symbol_);
        userBurn = burnByUsers[token][msg.sender];
    }

    function isFullyMinted(address token) public view returns (bool) {
        require(token != address(0));
        return ERC20(token).totalSupply() == Capped(token).cap();
    }

    function deployToken(
        string memory name_,
        string memory symbol_,
        uint256 supply_,
        uint256 batchSize_,
        uint256 cycleInterval_,
        uint256 startBlockNumber_
        // uint256 royaltyPct_
    ) external {
        bytes32 key = keccak256(abi.encodePacked(symbol_));
        require(tokens[key] == address(0), 'XF: symbol already in use');
        ERC20 newToken = new AuctionedToken(
            name_,
            symbol_,
            supply_,
            batchSize_,
            cycleInterval_,
            startBlockNumber_
            // royaltyPct_
        );
        Ownable(address(newToken)).transferOwnership(msg.sender);
        tokens[key] = address(newToken);
        allTokens.push(symbol_);
        deployers[address(newToken)] = msg.sender;
    }

    function burn(address user, uint256 amount) external {
        require(deployers[msg.sender] != address(0), 'XF: bad caller');
        _caller = msg.sender;
        IBurnableToken(xenVarStaker).burn(user, amount);
    }

    /*
    function collectDeployerReward(address token) external {
        require(deployers[token] == msg.sender, 'XF: not a deployer');
        require(isFullyMinted(token), 'XF: token not fully minted');
        require(burnByTokens[token] > BURN_REWARD_THRESHOLD, 'XF: threshold not attained');
        require(deployers[token] != DEPLOYER_COLLECTED, 'XF: already collected');
        // txn.mint(msg.sender, DEPLOYER_TXN_REWARD);
        deployers[token] = DEPLOYER_COLLECTED;
    }

    function collectUserReward(address user) external {
        require(deployers[msg.sender] != address(0), 'XF: bad caller');
        require(isFullyMinted(msg.sender), 'XF: token not fully minted');
        require(burnByTokens[msg.sender] > BURN_REWARD_THRESHOLD, 'XF: threshold not attained');
        require(burnByUsers[msg.sender][user] != USER_COLLECTED, 'XF: already collected');
        // txn.mint(user, (USERS_TXN_REWARD * burnByUsers[msg.sender][user]) / burnByTokens[msg.sender]);
        burnByUsers[msg.sender][user] = USER_COLLECTED;
    }
    */

    // INTERFACES & STANDARDS
    // IERC165 IMPLEMENTATION

    /**
        @dev confirms support for IERC-165 and IBurnRedeemable interfaces
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return interfaceId == type(IBurnRedeemable).interfaceId ||
        interfaceId == type(IERC165).interfaceId;
    }

    function onTokenBurned(address user, uint256 amount) external {
        require(msg.sender == xenVarStaker, 'XF: illegal caller');
        require(_caller != address(0), 'XF: illegal state');
        IBurnRedeemable(_caller).onTokenBurned(user, amount);
        burnByTokens[_caller] += amount;
        burnByUsers[_caller][user] += amount;
        _caller = address(0);
    }

}
