// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@faircrypto/xen-crypto/contracts/interfaces/IBurnRedeemable.sol";
import "@faircrypto/xen-crypto/contracts/interfaces/IBurnableToken.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./interfaces/XENAware.sol";
import "./XENBurner.sol";
import "./interfaces/IVarStakingToken.sol";

contract XENVarStaker is XENBurner, IVarStakingToken {

    struct VarStake {
        uint256 amount;
        uint256 term;
        uint256 startPeriod;
    }

    uint256 public constant XEN_YIELD_PCT = 5;
    uint256 public constant PERIOD_DURATION_TS = 24 * 3_600; /* 1 day(s) */

    mapping(uint256 => address) public burners;
    uint256 public burnerCount;

    mapping(uint256 => uint256) public burns;
    mapping(uint256 => uint256) public yields;
    mapping(uint256 => uint256) public balances;

    uint256 public lastBurnPeriod;
    uint256 public lastPeriodTs;
    uint256 public currentPeriod;

    mapping(address => mapping(uint256 => VarStake)) public stakes;
    mapping(address => uint256) public stakeCounter;

    mapping(uint256 => uint256) public stakesByPeriod;
    mapping(uint256 => uint256) public accStakesByPeriod;
    mapping(uint256 => uint256) public accPowerByPeriod;
    mapping(uint256 => uint256) public pendingWithdrawalsByPeriod;
    mapping(uint256 => uint256) public accPaidByPeriod;

    uint256 public immutable genesisTs;

    // state management + guard
    address private _caller;

    event Staked(address indexed user, uint256 indexed period, uint256 amount);
    event Unstaked(address indexed user, uint256 indexed period, uint256 amount, uint256 yield);

    constructor(address xenCrypto_) XENBurner(xenCrypto_) {
        genesisTs = block.timestamp;
        lastPeriodTs = genesisTs;
    }

    function registerBurner(address burner_) external {
        require(burner_ != address(0), 'VarStaker: bad burner address');
        // TODO: verify that Burner supports min interface

        burners[++burnerCount] = burner_;
    }

    function _checkAndAdjustPeriod() private {
        uint256 adj = (block.timestamp - lastPeriodTs) / PERIOD_DURATION_TS;
        if (adj > 0) {
            require(accPowerByPeriod[currentPeriod]
            + accStakesByPeriod[currentPeriod]
                >= pendingWithdrawalsByPeriod[currentPeriod], 'VarStaker: potential overflow?');
            accStakesByPeriod[currentPeriod + adj] += accStakesByPeriod[currentPeriod];
            accPowerByPeriod[currentPeriod + adj] += (
                accPowerByPeriod[currentPeriod]
                + accStakesByPeriod[currentPeriod]
                - pendingWithdrawalsByPeriod[currentPeriod]
            );
            balances[currentPeriod] = (balances[currentPeriod] + burns[currentPeriod] > yields[currentPeriod])
                ? balances[currentPeriod] + burns[currentPeriod] - yields[currentPeriod]
                : 0;
            balances[currentPeriod + adj] = balances[currentPeriod];
            currentPeriod += adj;
            lastPeriodTs = block.timestamp;
        }
    }

    function burn(address user, uint256 amount) external virtual override {
        // TODO: check if the caller is authorized

        // check if the caller is IBurnRedeemable
        bytes4 interfaceId = type(IBurnRedeemable).interfaceId;
        require(IERC165(msg.sender).supportsInterface(interfaceId));

        // check if we need to start new period
        _checkAndAdjustPeriod();
        uint256 yieldAmount = amount * XEN_YIELD_PCT / 100;
        ERC20(xenCrypto).transferFrom(user, address(this), yieldAmount);
        amount -= yieldAmount;
        _caller = msg.sender;
        burns[currentPeriod] += yieldAmount;
        IBurnableToken(xenCrypto).burn(user, amount);
    }

    function onTokenBurned(address user, uint256 amount) external virtual override {
        require(msg.sender == xenCrypto, 'VarStaker: illegal caller');
        require(_caller != address(0), 'VarStaker: illegal state');

        IBurnRedeemable(_caller).onTokenBurned(user, amount);
        lastBurnPeriod = currentPeriod;
        _caller = address(0);
    }

    // @dev initiates new stake or tops up existing one
    function stake(uint256 amount, uint256 term) external {
        require(amount > 0, 'VarStaker: amount too low');
        require(term > 0, 'VarStaker: term too low');

        // check if we need to start new period
        _checkAndAdjustPeriod();

        ERC20(xenCrypto).transferFrom(msg.sender, address(this), amount);
        uint256 startPeriod = currentPeriod + 1;
        stakesByPeriod[startPeriod] += amount;
        accStakesByPeriod[startPeriod] += amount;
        VarStake memory userStake = VarStake({
            amount: amount,
            term: term,
            startPeriod: startPeriod
        });
        stakeCounter[msg.sender]++;
        stakes[msg.sender][stakeCounter[msg.sender]] = userStake;
        emit Staked(msg.sender, startPeriod, amount);
    }

    // TODO: remove after testing
    function getAccStakesDelta(uint256 startPeriod, uint256 endPeriod) public view returns (uint256) {
        return (accPowerByPeriod[endPeriod] > accPowerByPeriod[startPeriod])
            ? accPowerByPeriod[endPeriod] - accPowerByPeriod[startPeriod]
            : 0;
    }

    function getPaidForThePeriod(uint256 startPeriod, uint256 endPeriod) public view returns (uint256) {
        return accPaidByPeriod[endPeriod] - accPaidByPeriod[startPeriod];
    }

    function _calcYield(VarStake memory stake_) private view returns (uint256) {
        if (stake_.startPeriod >= currentPeriod) {
            return 0;
        }
        uint256 startPeriod = (lastBurnPeriod < (stake_.startPeriod - 1)) ? lastBurnPeriod : stake_.startPeriod - 1;
        uint256 endPeriod = currentPeriod;
        uint256 accPowerDelta = getAccStakesDelta(startPeriod, endPeriod);
        if (accPowerDelta == 0) {
            return 0;
        }
        uint256 term = ((currentPeriod - stake_.startPeriod) > 0) ? currentPeriod - stake_.startPeriod : 0;
        if (term == 0) {
            return 0;
        }
        if (accPowerDelta > 0) {
            uint256 yield = (balances[currentPeriod - 1] * (stake_.amount * term)) / accPowerDelta;
            if (yield > 1) {
                return(yield - 2);
            } else {
                return yield;
            }
        } else {
            return 0;
        }
    }

    // TODO: add implementation ??? or remove ???
    function withdrawYield() external {
        // TODO calculate reward
    }

    // TODO: add implementation
    function withdraw(uint256 index) external {
        // check if we need to start new period
        _checkAndAdjustPeriod();

        require(index > 0, 'VarStaker: bad index');
        uint256 counter = stakeCounter[msg.sender];
        require(counter > 0, 'VarStaker: user has no stakes');
        require(index <= counter, 'VarStaker: bad index!');
        VarStake memory userStake = stakes[msg.sender][index];
        uint256 amount = userStake.amount;
        require(amount > 0, 'VarStaker: no stake found');

        uint256 term = (currentPeriod > userStake.startPeriod) ? currentPeriod - userStake.startPeriod : 0;
        uint256 yield = (term > 0) ? _calcYield(userStake) : 0;
        // TODO: combine amount + yield transfer
        uint256 balance = ERC20(xenCrypto).balanceOf(address(this));
        ERC20(xenCrypto).transfer(msg.sender, balance >= amount + yield ? amount + yield : balance);

        accStakesByPeriod[currentPeriod] -= amount;
        pendingWithdrawalsByPeriod[currentPeriod] += (amount * term);
        yields[currentPeriod] += yield;

        // cleanup stake
        stakes[msg.sender][index] = stakes[msg.sender][counter];
        // TODO: check if it has any effect ???
        // delete stakes[msg.sender][counter];
        stakeCounter[msg.sender]--;
        emit Unstaked(msg.sender, currentPeriod, amount, yield);
    }

    // TODO: keep or remove ???
    function housekeeping() external {
        _checkAndAdjustPeriod();
    }

    function getYieldEstimate(uint256 index) external view returns (uint256) {
        VarStake memory userStake = stakes[msg.sender][index];
        require(userStake.amount > 0, 'VarStaker: no stake ???');
        return _calcYield(userStake);
    }
}
